<script>
import WalletList from '@/components/WalletList.vue'
import Button from '@/components/Button.vue'
import { QrcodeStream, QrcodeDropZone, QrcodeCapture } from 'vue3-qrcode-reader'

export default {
    name: "Payment",
    components: {
        WalletList,
        Button,
        QrcodeStream,
        QrcodeDropZone,
        QrcodeCapture,
    },
    data () {
        return {
            darag_enter: false,
            scanner_open: false
        }
    },
    watch: {
        scanner_open () {
            if (this.scanner_open) {
                $('body').addClass('scanner_active');
            } else {
                $('body').removeClass('scanner_active');
            }
        }
    },
    created () {
        $('.line_wrapper .line').addClass('active')
        setInterval(() => {
            $('.line_wrapper .line').toggleClass('active')
        }, 1500);
    },
    methods: {
        saveFile () {
            console.log('Save file');
        },
        uploadFile (e) {
            if (e.target.files[0]) {
                let linkUrl = URL.createObjectURL(e.target.files[0]);
                this.$refs.bg_img.setAttribute('src', linkUrl);
                this.$refs.bg_img.style.display = 'block';
                console.log(linkUrl);
            }
        },
        handleDragOver (e) {
            e.preventDefault();
            this.darag_enter = true;
        },
        handleDrop (e) {
            e.preventDefault();
            this.$refs.file.files = e.dataTransfer.files;
            let linkUrl = URL.createObjectURL(e.dataTransfer.files[0]);
            this.$refs.bg_img.setAttribute('src', linkUrl);
            this.$refs.bg_img.style.display = 'block';
        },
        onDecode (decodedString) {
            console.log(decodedString);
        }
    }
}
</script>

